import React, { Suspense, useMemo } from "react";
// import reactDom from "react-dom";
// import * as THREE from 'three';
// import * as Fiber from '@react-three/fiber';
// import * as Drei from '@react-three/drei';
import { OrbitControls, Stage } from '@react-three/drei'
import { useRef, useState } from 'react';
import { Canvas, useFrame, useLoader } from '@react-three/fiber'
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { TorusBufferGeometry } from "three";


const Top = () => {

    function Box(props) {
        const mesh = useRef()
        const [hovered, setHover] = useState(false)
        const [active, setActive] = useState(false)

        useFrame((state, delta) => (mesh.current.rotation.x += 0.01))

        return (
            <mesh
                {...props}
                ref={mesh}
                castShadow
                receiveShadow
                scale={active ? 0.8 : 0.5}
                onClick={(event) => setActive(!active)}
                onPointerOver={(event) => setHover(true)}
                onPointerOut={(event) => setHover(false)}
            // onWheel={(event) => setActive(true)}
            >
                <boxBufferGeometry args={[3, 1, 1]} />
                <meshStandardMaterial color={hovered ? 'hotpink' : 'yellow'} />
            </mesh>
        )
    }

    function Triangle(props) {
        const mesh = useRef()
        return (
            <mesh
                {...props}
                ref={mesh}
                scale={0.5}
                >
                    <IcosahedronGeometry />
                    <meshBasicMaterial color="pink" />
                </mesh>
        )
    }

    const LoadModel = () => {

        const gltf = useLoader(GLTFLoader, "/only_book.glb")
        return (
            <group>
                <primitive scale={0.8} position={[0, 0, 0]}
                    castShadow
                    receiveShadow
                    object={gltf.scene} dispose={null} />
            </group>
        )
    }


    return (
        <>
            <h2>5次元日記</h2>
            <div className="canvas-area">
                <Suspense fallback={<span>loading...</span>}>
                    <Canvas
                        shadows
                        performance={{ min: 0.5 }}
                        dpr={[1, 2]}
                        camera={{ fov: 25, position: [-10, 11, -10] }}
                    >
                        {/* <Environment preset="sunset" background={false} /> */}
                        {/* <color attach="background" args={['#1e2243']} /> */}

                        {/* <ambientLight intensity={0.5} />
                        <pointLight position={[5, 5, 10]} intensity={0.5} />
                        <directionalLight position={[10, 10, 5]} color="#fff" intensity={1} /> */}
                        <Box position={[0, 2, 0]} />
                        <Triangle />
                        <Stage adjustCamera={false} environment="city">
                            <LoadModel />
                        </Stage>
                        <OrbitControls enablePan={true} enableZoom={true} enableRotate={true} />

                    </Canvas>
                </Suspense>
            </div>
        </>
    );
}
export default Top;
