import React, { Suspense, useEffect, useMemo } from "react";
import { OrbitControls, Environment } from '@react-three/drei';
import { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import * as THREE from 'three';
import PageNation from "../components/PageNation";

const Num2 = (props) => {

    function Particles({ count }) {
        const mesh = useRef()
        const light = useRef()

        const dummy = useMemo(() => new THREE.Object3D(), [])
        // Generate some random positions, speed factors and timings
        const particles = useMemo(() => {
            const temp = []
            for (let i = 0; i < count; i++) {
                const t = Math.random() * 100
                const factor = 20 + Math.random() * 100
                const speed = 0.01 + Math.random() / 200
                const xFactor = -50 + Math.random() * 100
                const yFactor = -50 + Math.random() * 100
                const zFactor = -50 + Math.random() * 100
                temp.push({ t, factor, speed, xFactor, yFactor, zFactor, mx: 0, my: 0 })
            }
            return temp
        }, [count])
        // The innards of this hook will run every frame
        useFrame((state) => {
            // Makes the light follow the mouse
            // light.current.position.set(mouse.current[0] / aspect, -mouse.current[1] / aspect, 0)
            // Run through the randomized data to calculate some movement
            particles.forEach((particle, i) => {
                let { t, factor, speed, xFactor, yFactor, zFactor } = particle
                // There is no sense or reason to any of this, just messing around with trigonometric functions
                t = particle.t += speed / 2
                const a = Math.cos(t) + Math.sin(t * 1) / 10
                const b = Math.sin(t) + Math.cos(t * 2) / 10
                const s = Math.cos(t)
                // particle.mx += (mouse.current[0] - particle.mx) * 0.01
                // particle.my += (mouse.current[1] * -1 - particle.my) * 0.01
                // Update the dummy object
                dummy.position.set(
                    (particle.mx / 10) * a + xFactor + Math.cos((t / 10) * factor) + (Math.sin(t * 1) * factor) / 10,
                    (particle.my / 10) * b + yFactor + Math.sin((t / 10) * factor) + (Math.cos(t * 2) * factor) / 10,
                    (particle.my / 10) * b + zFactor + Math.cos((t / 10) * factor) + (Math.sin(t * 3) * factor) / 10
                )
                dummy.scale.set(s, s, s)
                dummy.rotation.set(s * 5, s * 5, s * 5)
                dummy.updateMatrix()
                // And apply the matrix to the instanced item
                mesh.current.setMatrixAt(i, dummy.matrix)
            })
            // mesh.current.instanceMatrix.needsUpdate = true
        })
        return (
            <>
                <pointLight ref={light} distance={40} intensity={1} color="lightblue" />
                <instancedMesh ref={mesh} args={[null, null, count]}>
                    <dodecahedronGeometry args={[0.5, 0]} />
                    <meshPhongMaterial color="#fff" />
                </instancedMesh>
            </>
        )
    };



    return (
        <>
            <h2>{props.num} / 365</h2>
            <div className="daily-canvas">
                <Canvas
                    shadows
                    performance={{ min: 0.5 }}
                    dpr={[1, 2]}
                    camera={{ fov: 100, position: [0, 0, 5] }}
                >

                    {/* <ambientLight intensity={0.5} />
                        <pointLight position={[5, 5, 10]} intensity={0.5} />
                        <directionalLight position={[10, 10, 5]} color="#fff" intensity={1} /> */}
                    {/* <fog attach="fog" args={['#1e2243', 0, 15]} /> */}

                    <Suspense fallback={<span>loading...</span>}>

                    <color attach="background" args={['#1e2243']} />
                        {/* <Environment preset="sunset" background /> */}
                        <ambientLight />
                        {/* <Instances /> */}
                        {/* <Fireflies count={40}/> */}
                        {/* <Lines count={100} colors={['#A2CCB6', '#FCEEB5', '#EE786E', '#e0feff', 'lightpink', 'lightblue']} /> */}
                        <Particles count={50} />
                        <OrbitControls enablePan={true} enableZoom={true} enableRotate={true} />
                    </Suspense>
                </Canvas>
                <div className="essay">
                    <h3></h3>

                    <p></p>
                    <ul className="source">
                        <li>(1) </li>
                        <li>(2) <a href="https://www.cinra.net/interview/201404-mumgypsykawakami" target="_blank" rel="noreferrer">藤田貴大×川上未映子の叫び「まだ全然言い足りてない」</a> 2014/04/03</li>
                    </ul>
                    <p className="date">2021.09.23</p>
                </div>
            </div>
            <PageNation prev="1" next="3" />
        </>
    );
}

export default Num2;
