import React, { Suspense } from "react";
import { OrbitControls } from '@react-three/drei'
import { useRef, useState } from 'react';
import { Canvas, useFrame } from '@react-three/fiber'
import PageNation from "../components/PageNation";

const Num1 = (props) => {
    function Box(props) {
        const mesh = useRef()
        const [hovered, setHover] = useState(false)
        const [active, setActive] = useState(false)

        // useFrame((state, delta) => (mesh.current.rotation.x += 0.01))

        return (
            <mesh
                {...props}
                ref={mesh}
                castShadow
                receiveShadow
                scale={active ? 0.8 : 0.5}
                onClick={(event) => setActive(!active)}
                onPointerOver={(event) => setHover(true)}
                onPointerOut={(event) => setHover(false)}
            >
                <boxBufferGeometry args={[2, 2, 2]} />
                <meshStandardMaterial color={hovered ? 'hotpink' : 'yellow'} />
            </mesh>
        )
    }


    return (
        <>
        <h2>{props.num} / 365</h2>
        <div className="daily-canvas">
            <Suspense fallback={<span>loading...</span>}>
                <Canvas
                    shadows
                    performance={{ min: 0.5 }}
                    dpr={[1, 2]}
                    camera={{ fov: 45, position: [10, 10, 10] }}
                >

                        {/* <Environment preset="sunset" background={false} /> */}
                        {/* <color attach="background" args={['#1e2243']} /> */}

                        {/* <ambientLight intensity={0.5} />
                        <pointLight position={[5, 5, 10]} intensity={0.5} />
                        <directionalLight position={[10, 10, 5]} color="#fff" intensity={1} /> */}
                    <color attach="background" args={['#1e2243']} />

                        <ambientLight />
                        <pointLight position={[10, 10, 10]} />
                        <directionalLight position={[10, 15, 10]} color="white" />
                        <Box position={[0, 0.5, 0]} />
                        <OrbitControls enablePan={true} enableZoom={true} enableRotate={true} />

                </Canvas>
            </Suspense>
            <p>すべての時間軸に抗う とは</p>
            <p>半年ほど前、「すべての時間軸に抗う人へ」というタイトルで、作品集を作った。CLOやBlenderといった3DCGsakusei</p>
        </div>
        <PageNation prev="archive" next="2"/>
        </>
    );
}

export default Num1;
