import React, { Suspense } from "react";
// import reactDom from "react-dom";
// import * as THREE from 'three';
// import * as Fiber from '@react-three/fiber';
// import * as Drei from '@react-three/drei';
import { OrbitControls, Stage, Html } from '@react-three/drei'
import { useRef, useState } from 'react';
import { Canvas, useFrame, useLoader } from '@react-three/fiber'
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";



const Top = () => {

    function Box(props) {
        const mesh = useRef()
        const [hovered, setHover] = useState(false)
        const [active, setActive] = useState(false)

        useFrame((state, delta) => (mesh.current.rotation.x += 0.01))

        return (
            <mesh
                {...props}
                ref={mesh}
                castShadow
                receiveShadow
                scale={active ? 0.8 : 0.5}
                onClick={(event) => setActive(!active)}
                onPointerOver={(event) => setHover(true)}
                onPointerOut={(event) => setHover(false)}
            // onWheel={(event) => setActive(true)}
            >
                <boxBufferGeometry args={[3, 1, 1]} />
                <meshStandardMaterial color={hovered ? 'hotpink' : 'yellow'} />
            </mesh>
        )
    }

    function Triangle(props) {
        const mesh = useRef()
        const [hovered, setHover] = useState(false)
        useFrame((state, delta) => (mesh.current.rotation.z += 0.01))

        return (
            <mesh
                {...props}
                ref={mesh}
                scale={0.5}
                onPointerOver={(event) => setHover(true)}
                onPointerOut={(event) => setHover(false)}
                rotation={hovered ? '0' : [Math.PI / -1, 2, 0]}
            >
                <torusGeometry tubularSegments={3} args={[3, 0.1, 2, 3]} />
                <meshBasicMaterial color="pink" />

                <Html distanceFactor={10}>
                    <div className="content">
                        <a href="./1" className="content-link">Day 1</a>
                    </div>
                </Html>
            </mesh>
        )
    }

    function Plane(props) {
        const mesh = useRef()
        useFrame((state, delta) => (mesh.current.rotation.z += 0.005))

        return (
            <mesh
                {...props}
                ref={mesh}
                scale={0.5}
                doubleSide={true}
                rotation={[Math.PI / 2, 0, 0]}
            >
                <icosahedronGeometry />
                <meshPhongMaterial color="orange" />
            </mesh>
        )
    }

    const LoadModel = () => {

        const gltf = useLoader(GLTFLoader, "/only_book.glb")
        return (
            <group>
                <primitive scale={0.8} position={[0, 0, 0]}
                    castShadow
                    receiveShadow
                    object={gltf.scene} dispose={null} />
            </group>
        )
    }


    return (
        <>
            <h2>See the picture</h2>
            <div className="canvas-area">
                <Suspense fallback={<span>loading...</span>}>
                    <Canvas
                        shadows
                        performance={{ min: 0.5 }}
                        dpr={[1, 2]}
                        camera={{ fov: 40, position: [-10, 11, -10] }}
                    >
                        <Box position={[0, 2, 0]} />
                        <Triangle position={[-3, 3, -2]} />
                        <Stage adjustCamera={false} environment="city">
                            <Plane position={[3, 1.5, -3]} />
                            <LoadModel />
                        </Stage>
                        <OrbitControls enablePan={true} enableZoom={true} enableRotate={true} />

                    </Canvas>
                </Suspense>
            </div>
        </>
    );
}
export default Top;
